import{_ as o,c as r,o as t,ag as i}from"./chunks/framework.DUDLMHYb.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"DSA/Leetcode/3496-minimum-number-of-seconds-to-make-mountain-height-zero/README.md","filePath":"DSA/Leetcode/3496-minimum-number-of-seconds-to-make-mountain-height-zero/README.md"}'),n={name:"DSA/Leetcode/3496-minimum-number-of-seconds-to-make-mountain-height-zero/README.md"};function s(a,e,d,m,c,h){return t(),r("div",null,[...e[0]||(e[0]=[i('<h2 id="_3496-minimum-number-of-seconds-to-make-mountain-height-zero" tabindex="-1">3496. Minimum Number of Seconds to Make Mountain Height Zero <a class="header-anchor" href="#_3496-minimum-number-of-seconds-to-make-mountain-height-zero" aria-label="Permalink to &quot;3496. Minimum Number of Seconds to Make Mountain Height Zero&quot;">​</a></h2><p><strong>Difficulty:</strong> Medium</p><hr><p>You are given an integer <code>mountainHeight</code> denoting the height of a mountain.</p><p>You are also given an integer array <code>workerTimes</code> representing the work time of workers in <strong>seconds</strong>.</p><p>The workers work <strong>simultaneously</strong> to <strong>reduce</strong> the height of the mountain. For worker <code>i</code>:</p><ul><li>To decrease the mountain&#39;s height by <code>x</code>, it takes <code>workerTimes[i] + workerTimes[i] * 2 + ... + workerTimes[i] * x</code> seconds. For example: <ul><li>To reduce the height of the mountain by 1, it takes <code>workerTimes[i]</code> seconds.</li><li>To reduce the height of the mountain by 2, it takes <code>workerTimes[i] + workerTimes[i] * 2</code> seconds, and so on.</li></ul></li></ul><p>Return an integer representing the <strong>minimum</strong> number of seconds required for the workers to make the height of the mountain 0.</p><h3 id="example-1" tabindex="-1">Example 1: <a class="header-anchor" href="#example-1" aria-label="Permalink to &quot;Example 1:&quot;">​</a></h3><p><strong>Input:</strong> <code>mountainHeight = 4, workerTimes = [2,1,1]</code></p><p><strong>Output:</strong> <code>3</code></p><p><strong>Explanation:</strong></p><p>One way the height of the mountain can be reduced to 0 is:</p><ul><li>Worker 0 reduces the height by 1, taking <code>workerTimes[0] = 2</code> seconds.</li><li>Worker 1 reduces the height by 2, taking <code>workerTimes[1] + workerTimes[1] * 2 = 3</code> seconds.</li><li>Worker 2 reduces the height by 1, taking <code>workerTimes[2] = 1</code> second.</li></ul><p>Since they work simultaneously, the minimum time needed is <code>max(2, 3, 1) = 3</code> seconds.</p><h3 id="example-2" tabindex="-1">Example 2: <a class="header-anchor" href="#example-2" aria-label="Permalink to &quot;Example 2:&quot;">​</a></h3><p><strong>Input:</strong> <code>mountainHeight = 10, workerTimes = [3,2,2,4]</code></p><p><strong>Output:</strong> <code>12</code></p><p><strong>Explanation:</strong></p><ul><li>Worker 0 reduces the height by 2, taking <code>workerTimes[0] + workerTimes[0] * 2 = 9</code> seconds.</li><li>Worker 1 reduces the height by 3, taking <code>workerTimes[1] + workerTimes[1] * 2 + workerTimes[1] * 3 = 12</code> seconds.</li><li>Worker 2 reduces the height by 3, taking <code>workerTimes[2] + workerTimes[2] * 2 + workerTimes[2] * 3 = 12</code> seconds.</li><li>Worker 3 reduces the height by 2, taking <code>workerTimes[3] + workerTimes[3] * 2 = 12</code> seconds.</li></ul><p>The number of seconds needed is <code>max(9, 12, 12, 12) = 12</code> seconds.</p><h3 id="example-3" tabindex="-1">Example 3: <a class="header-anchor" href="#example-3" aria-label="Permalink to &quot;Example 3:&quot;">​</a></h3><p><strong>Input:</strong> <code>mountainHeight = 5, workerTimes = [1]</code></p><p><strong>Output:</strong> <code>15</code></p><p><strong>Explanation:</strong></p><p>There is only one worker in this example, so the answer is <code>workerTimes[0] + workerTimes[0] * 2 + workerTimes[0] * 3 + workerTimes[0] * 4 + workerTimes[0] * 5 = 15</code>.</p><h3 id="constraints" tabindex="-1">Constraints: <a class="header-anchor" href="#constraints" aria-label="Permalink to &quot;Constraints:&quot;">​</a></h3><ul><li><code>1 &lt;= mountainHeight &lt;= 10^5</code></li><li><code>1 &lt;= workerTimes.length &lt;= 10^4</code></li><li><code>1 &lt;= workerTimes[i] &lt;= 10^6</code></li></ul>',28)])])}const g=o(n,[["render",s]]);export{u as __pageData,g as default};
